# coding=utf-8
from datetime import date

def generator( file,url, changefreq = 'daily' , priority = 1 , date = date.today().strftime("%Y-%m-%d")):
	file.write('\t%s\n' % '<url>')
	file.write('\t\t%s' % '<loc>')
	file.write('%s' % url)
	file.write('%s\n' % '</loc>')
	file.write('\t\t%s' % '<lastmod>')
	file.write('%s' % date)
	file.write('%s\n' % '</lastmod>')
	file.write('\t\t%s' % '<changefreq>')
	file.write('%s' % changefreq)
	file.write('%s\n' % '</changefreq>')
	file.write('\t\t%s' % '<priority>')
	file.write('%s' % priority)
	file.write('%s\n' % '</priority>')
	file.write('\t%s\n' % '</url>')


